INSERT INTO public.calc (id, title, payload) VALUES (2, 'Скорректированный прирост количества (CCI) для переливания тромбоцитов', '{"title": "\u0421\u043a\u043e\u0440\u0440\u0435\u043a\u0442\u0438\u0440\u043e\u0432\u0430\u043d\u043d\u044b\u0439 \u043f\u0440\u0438\u0440\u043e\u0441\u0442 \u043a\u043e\u043b\u0438\u0447\u0435\u0441\u0442\u0432\u0430 (CCI) \u0434\u043b\u044f \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u044f \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432", "info": [{"title": "\u043a\u043e\u0433\u0434\u0430 \u0438\u0441\u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u0442\u044c", "text": "\u041f\u0440\u0438\u043c\u0435\u043d\u0435\u043d\u0438\u0435 \u0443 \u043f\u0430\u0446\u0438\u0435\u043d\u0442\u043e\u0432, \u043f\u0435\u0440\u0435\u043d\u0435\u0441\u0448\u0438\u0445 \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u0435 \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432."}, {"title": "\u0436\u0435\u043c\u0447\u0443\u0436\u0438\u043d\u044b \u0438 \u043f\u043e\u0434\u0432\u043e\u0434\u043d\u044b\u0435 \u043a\u0430\u043c\u043d\u0438", "text": "\n\u0421\u043a\u043e\u0440\u0440\u0435\u043a\u0442\u0438\u0440\u043e\u0432\u0430\u043d\u043d\u044b\u0439 \u043f\u0440\u0438\u0440\u043e\u0441\u0442 \u043a\u043e\u043b\u0438\u0447\u0435\u0441\u0442\u0432\u0430 \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432 (CCI) \u044f\u0432\u043b\u044f\u0435\u0442\u0441\u044f \u043c\u0435\u0440\u043e\u0439 \u043e\u0436\u0438\u0434\u0430\u0435\u043c\u043e\u0433\u043e \u0443\u0432\u0435\u043b\u0438\u0447\u0435\u043d\u0438\u044f \u043a\u043e\u043b\u0438\u0447\u0435\u0441\u0442\u0432\u0430 \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432 \u043f\u043e\u0441\u043b\u0435 \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u044f \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432. \u201c\u0423\u0432\u0435\u043b\u0438\u0447\u0435\u043d\u0438\u0435 \u043a\u043e\u043b\u0438\u0447\u0435\u0441\u0442\u0432\u0430\u201d \u043e\u0442\u043d\u043e\u0441\u0438\u0442\u0441\u044f \u043a \u0443\u0432\u0435\u043b\u0438\u0447\u0435\u043d\u0438\u044e \u043a\u043e\u043b\u0438\u0447\u0435\u0441\u0442\u0432\u0430 \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432 \u043f\u043e\u0441\u043b\u0435 \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u044f. \u201c\u041a\u043e\u0440\u0440\u0435\u043a\u0446\u0438\u044f\u201d \u043e\u0441\u043d\u043e\u0432\u0430\u043d\u0430 \u043d\u0430 \u0440\u0430\u0437\u043c\u0435\u0440\u0435 \u043f\u0430\u0446\u0438\u0435\u043d\u0442\u0430 \u0438 \u043a\u043e\u043b\u0438\u0447\u0435\u0441\u0442\u0432\u0435 \u043f\u0435\u0440\u0435\u043b\u0438\u0442\u044b\u0445 \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432.\u00a0\n\u0420\u0435\u0444\u0440\u0430\u043a\u0442\u0435\u0440\u043d\u043e\u0441\u0442\u044c = \u043d\u0435\u0441\u043f\u043e\u0441\u043e\u0431\u043d\u043e\u0441\u0442\u044c \u0434\u043e\u0441\u0442\u0438\u0447\u044c \u043f\u0440\u0438\u0435\u043c\u043b\u0435\u043c\u043e\u0433\u043e \u0443\u0432\u0435\u043b\u0438\u0447\u0435\u043d\u0438\u044f \u043a\u043e\u043b\u0438\u0447\u0435\u0441\u0442\u0432\u0430 \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432 \u043f\u043e\u0441\u043b\u0435 \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u044f \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432 \u043f\u043e \u043a\u0440\u0430\u0439\u043d\u0435\u0439 \u043c\u0435\u0440\u0435 \u0432 \u0434\u0432\u0443\u0445 \u0441\u043b\u0443\u0447\u0430\u044f\u0445 \u0438 \u043e\u0442\u0441\u0443\u0442\u0441\u0442\u0432\u0438\u0435 \u0430\u043b\u044c\u0442\u0435\u0440\u043d\u0430\u0442\u0438\u0432\u043d\u043e\u0439 \u043f\u0440\u0438\u0447\u0438\u043d\u044b \u0440\u0435\u0444\u0440\u0430\u043a\u0442\u0435\u0440\u043d\u043e\u0441\u0442\u0438, \u0442\u0430\u043a\u043e\u0439 \u043a\u0430\u043a \u043b\u0438\u0445\u043e\u0440\u0430\u0434\u043a\u0430, \u0441\u0435\u043f\u0441\u0438\u0441, \u0414\u0412\u0421-\u0441\u0438\u043d\u0434\u0440\u043e\u043c, \u043a\u0440\u043e\u0432\u043e\u0442\u0435\u0447\u0435\u043d\u0438\u0435, \u0441\u043f\u043b\u0435\u043d\u043e\u043c\u0435\u0433\u0430\u043b\u0438\u044f \u0438\u043b\u0438 \u043b\u0435\u043a\u0430\u0440\u0441\u0442\u0432\u0435\u043d\u043d\u043e\u0435 \u0432\u0437\u0430\u0438\u043c\u043e\u0434\u0435\u0439\u0441\u0442\u0432\u0438\u0435 (\u043d\u0430\u043f\u0440\u0438\u043c\u0435\u0440, \u0430\u043c\u0444\u043e\u0442\u0435\u0440\u0438\u0446\u0438\u043d \u0412).\n\u0420\u0435\u0430\u043a\u0446\u0438\u044f \u043d\u0430 \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u0435 \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432 \u043e\u0431\u044b\u0447\u043d\u043e \u0438\u0437\u043c\u0435\u0440\u044f\u0435\u0442\u0441\u044f \u0432 \u0442\u0435\u0447\u0435\u043d\u0438\u0435 \u043e\u0434\u043d\u043e\u0433\u043e \u0447\u0430\u0441\u0430. \u041e\u0434\u043d\u0430\u043a\u043e \u043e\u0431\u0440\u0430\u0437\u0435\u0446, \u0432\u0437\u044f\u0442\u044b\u0439 \u0447\u0435\u0440\u0435\u0437 10 \u043c\u0438\u043d\u0443\u0442 \u043f\u043e\u0441\u043b\u0435 \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u044f, \u0434\u0430\u0435\u0442 \u0430\u043d\u0430\u043b\u043e\u0433\u0438\u0447\u043d\u0443\u044e \u0438\u043d\u0444\u043e\u0440\u043c\u0430\u0446\u0438\u044e, \u0438 \u0435\u0433\u043e \u043c\u043e\u0436\u0435\u0442 \u0431\u044b\u0442\u044c \u043b\u0435\u0433\u0447\u0435 \u043f\u043e\u043b\u0443\u0447\u0438\u0442\u044c \u0432 \u043e\u0431\u044b\u0447\u043d\u043e\u043c \u0440\u0435\u0436\u0438\u043c\u0435.\u00a0\u0410\u043b\u044c\u0442\u0435\u0440\u043d\u0430\u0442\u0438\u0432\u043d\u043e, \u0447\u0435\u0440\u0435\u0437 20 \u0447\u0430\u0441\u043e\u0432 \u043f\u043e\u0441\u043b\u0435 \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u044f \u043a\u0440\u043e\u0432\u0438 \u043c\u043e\u0436\u043d\u043e \u0438\u0437\u043c\u0435\u0440\u0438\u0442\u044c \u043a\u043e\u043b\u0438\u0447\u0435\u0441\u0442\u0432\u043e \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432.\n"}, {"title": "\u0437\u0430\u0447\u0435\u043c \u0438\u0441\u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u0442\u044c", "text": "\nCCI \u044f\u0432\u043b\u044f\u0435\u0442\u0441\u044f \u0431\u043e\u043b\u0435\u0435 \u0442\u043e\u0447\u043d\u044b\u043c \u043f\u043e\u043a\u0430\u0437\u0430\u0442\u0435\u043b\u0435\u043c \u0440\u0435\u0444\u0440\u0430\u043a\u0442\u0435\u0440\u043d\u043e\u0441\u0442\u0438, \u043f\u043e\u0441\u043a\u043e\u043b\u044c\u043a\u0443 \u043e\u043d \u0443\u0447\u0438\u0442\u044b\u0432\u0430\u0435\u0442 \u043a\u043e\u043b\u0438\u0447\u0435\u0441\u0442\u0432\u043e \u043f\u0435\u0440\u0435\u043b\u0438\u0442\u044b\u0445 \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432 \u0438 \u043e\u0431\u044a\u0435\u043c \u043a\u0440\u043e\u0432\u0438 \u043f\u0430\u0446\u0438\u0435\u043d\u0442\u0430.\n\u041c\u043e\u0436\u0435\u0442 \u043e\u043f\u0440\u0435\u0434\u0435\u043b\u044f\u0442\u044c \u0440\u0435\u0448\u0435\u043d\u0438\u0435 \u043e \u043f\u0440\u0438\u043c\u0435\u043d\u0435\u043d\u0438\u0438 \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432 \u0441 \u0443\u043b\u0443\u0447\u0448\u0435\u043d\u043d\u043e\u0439 \u0441\u043e\u0432\u043c\u0435\u0441\u0442\u0438\u043c\u043e\u0441\u0442\u044c\u044e (\u0442.\u0435. \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432, \u0441\u043e\u043e\u0442\u0432\u0435\u0442\u0441\u0442\u0432\u0443\u044e\u0449\u0438\u0445 HLA).\n"}, {"title": "\u0441\u043b\u0435\u0434\u0443\u044e\u0449\u0438\u0435 \u0448\u0430\u0433\u0438", "text": "\n\u041a\u043e\u0433\u0434\u0430 \u043d\u0435 \u0443\u0434\u0430\u0435\u0442\u0441\u044f \u043d\u0430\u0439\u0442\u0438 \u0441\u043e\u0432\u043c\u0435\u0441\u0442\u0438\u043c\u044b\u0445 \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u043e\u0432 \u0434\u043b\u044f \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u044f \u043f\u0430\u0446\u0438\u0435\u043d\u0442\u0443 \u0441 \u0440\u0435\u0444\u0440\u0430\u043a\u0442\u0435\u0440\u043d\u043e\u0441\u0442\u044c\u044e \u043a \u0442\u0440\u043e\u043c\u0431\u043e\u0446\u0438\u0442\u0430\u043c, \u043c\u043e\u0436\u043d\u043e \u043f\u043e\u043f\u0440\u043e\u0431\u043e\u0432\u0430\u0442\u044c \u0430\u043b\u044c\u0442\u0435\u0440\u043d\u0430\u0442\u0438\u0432\u043d\u044b\u0435 \u043c\u0435\u0440\u044b, \u0447\u0442\u043e\u0431\u044b \u043e\u0441\u0442\u0430\u043d\u043e\u0432\u0438\u0442\u044c \u0438\u043b\u0438 \u043f\u0440\u0435\u0434\u043e\u0442\u0432\u0440\u0430\u0442\u0438\u0442\u044c \u043a\u0440\u043e\u0432\u043e\u0442\u0435\u0447\u0435\u043d\u0438\u0435, \u043d\u043e \u043e\u043d\u0438 \u043f\u0440\u0438\u043d\u043e\u0441\u044f\u0442 \u043d\u0435\u043f\u0440\u0435\u0434\u0441\u043a\u0430\u0437\u0443\u0435\u043c\u0443\u044e \u043f\u043e\u043b\u044c\u0437\u0443.\n\u0412\u0432\u0435\u0434\u0435\u043d\u0438\u0435 \u0430\u043d\u0442\u0438\u0444\u0438\u0431\u0440\u0438\u043d\u043e\u043b\u0438\u0442\u0438\u0447\u0435\u0441\u043a\u0438\u0445 \u0441\u0440\u0435\u0434\u0441\u0442\u0432, \u0442\u0430\u043a\u0438\u0445 \u043a\u0430\u043a \u03b5-\u0430\u043c\u0438\u043d\u043e\u043a\u0430\u043f\u0440\u043e\u043d\u043e\u0432\u0430\u044f \u043a\u0438\u0441\u043b\u043e\u0442\u0430 (\u0410\u043c\u0438\u043a\u0430\u0440), \u043b\u0438\u0431\u043e \u0432\u043d\u0443\u0442\u0440\u0438\u0432\u0435\u043d\u043d\u043e, \u043b\u0438\u0431\u043e, \u0432 \u0441\u043b\u0443\u0447\u0430\u0435 \u043a\u0440\u043e\u0432\u043e\u0442\u0435\u0447\u0435\u043d\u0438\u044f \u0438\u0437 \u0434\u0435\u0441\u0435\u043d, \u0432 \u0432\u0438\u0434\u0435 \u0436\u0438\u0434\u043a\u043e\u0441\u0442\u0438 \u0434\u043b\u044f \u043f\u043e\u043b\u043e\u0441\u043a\u0430\u043d\u0438\u044f \u0440\u0442\u0430, \u043c\u043e\u0436\u0435\u0442 \u0441\u043f\u043e\u0441\u043e\u0431\u0441\u0442\u0432\u043e\u0432\u0430\u0442\u044c \u0441\u043e\u0445\u0440\u0430\u043d\u0435\u043d\u0438\u044e \u043e\u0431\u0440\u0430\u0437\u043e\u0432\u0430\u0432\u0448\u0435\u0433\u043e\u0441\u044f \u0441\u0433\u0443\u0441\u0442\u043a\u0430.\n\u0423\u0447\u0438\u0442\u044b\u0432\u0430\u0439\u0442\u0435 \u0438\u0441\u0445\u043e\u0434\u043d\u044b\u0435 \u0444\u0430\u043a\u0442\u043e\u0440\u044b \u043f\u0430\u0446\u0438\u0435\u043d\u0442\u0430, \u0432\u043a\u043b\u044e\u0447\u0430\u044f \u0432\u043e\u0437\u0440\u0430\u0441\u0442, \u043f\u043e\u043b, \u0440\u043e\u0441\u0442/\u0432\u0435\u0441, \u0438\u0441\u0442\u043e\u0440\u0438\u044e \u0431\u0435\u0440\u0435\u043c\u0435\u043d\u043d\u043e\u0441\u0442\u0438 \u0438 \u043f\u0440\u0435\u0434\u044b\u0434\u0443\u0449\u0435\u0435 \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u0435.\n\u0412\u043e\u0437\u043c\u043e\u0436\u043d\u044b\u0435 \u0445\u0430\u0440\u0430\u043a\u0442\u0435\u0440\u0438\u0441\u0442\u0438\u043a\u0438 \u043f\u0430\u0446\u0438\u0435\u043d\u0442\u0430, \u043f\u043e\u0434\u043b\u0435\u0436\u0430\u0449\u0438\u0435 \u043e\u0446\u0435\u043d\u043a\u0435 \u043f\u0440\u0438 \u043a\u0430\u0436\u0434\u043e\u043c \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u0438:\n\n\u0412\u0432\u0435\u0434\u0435\u043d\u0438\u0435 \u0430\u043c\u0444\u043e\u0442\u0435\u0440\u0438\u0446\u0438\u043d\u0430, \u0433\u0435\u043f\u0430\u0440\u0438\u043d\u0430 \u0438\u043b\u0438 \u03b3-\u0433\u043b\u043e\u0431\u0443\u043b\u0438\u043d\u0430.\n\u0421\u0442\u0430\u0442\u0443\u0441 \u0430\u043d\u0442\u0438\u0442\u0435\u043b \u043a HLA \u0438/\u0438\u043b\u0438 PLT \u0432 \u0431\u043b\u0438\u0436\u0430\u0439\u0448\u0435\u043c \u043e\u0431\u0440\u0430\u0437\u0446\u0435 \u043f\u0435\u0440\u0435\u0434 \u043f\u0435\u0440\u0435\u043b\u0438\u0432\u0430\u043d\u0438\u0435\u043c.\n\u041d\u0430\u043b\u0438\u0447\u0438\u0435 \u043a\u0440\u043e\u0432\u043e\u0442\u0435\u0447\u0435\u043d\u0438\u044f, \u043b\u0438\u0445\u043e\u0440\u0430\u0434\u043a\u0438, \u0438\u043d\u0444\u0435\u043a\u0446\u0438\u0438, \u0440\u0435\u0430